Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": { "name": "dataset" },

  "transform": [
    { "fold": ["Activos_BG1", "Pasivos_BG1", "Patrimonio_BG1"], "as": ["Componente", "Valor"] },
    { "calculate": "datum.Componente == ''Activos_BG1'' ? ''ACTIVOS'' : ''PASIVOS / PATRIMONIO''", "as": "Grupo" },
    { "calculate": "datum.Componente == ''Activos_BG1'' ? ''ACTIVOS'' : (datum.Componente == ''Pasivos_BG1'' ? ''PASIVOS'' : ''PATRIMONIO'')", "as": "Etiqueta" },
    { "calculate": "datum.Valor / 1000000", "as": "Valor_M" },
    { "calculate": "format(datum.Valor_M, ''.0f'') + '' M''", "as": "Valor_M_txt" },
    { "calculate": "datum.Valor / 2", "as": "Valor_mid" },

    { "calculate": "substring(datum.MesNombreAño, 0, 3)", "as": "MesAbr" },
    { "calculate": "toNumber(substring(datum.MesNombreAño, length(datum.MesNombreAño)-4, 4))", "as": "AnioNum" },
    { "calculate": "indexof([''Ene'',''Feb'',''Mar'',''Abr'',''May'',''Jun'',''Jul'',''Ago'',''Sep'',''Oct'',''Nov'',''Dic''], datum.MesAbr) + 1", "as": "MesNum" },
    { "calculate": "datum.AnioNum * 100 + datum.MesNum", "as": "OrdenYM" },

    { "calculate": "datum.Etiqueta == ''PASIVOS'' ? 0 : (datum.Etiqueta == ''PATRIMONIO'' ? 1 : -1)", "as": "OrdenSegmento" },

    {
      "window": [
        { "op": "sum", "field": "Valor", "as": "CumSum" }
      ],
      "sort": [{ "field": "OrdenSegmento", "order": "ascending" }],
      "groupby": ["MesNombreAño", "Grupo"],
      "frame": [null, 0]
    },
    { "calculate": "datum.CumSum - datum.Valor", "as": "Valor_start" },
    { "calculate": "datum.Valor_start + datum.Valor / 2", "as": "Valor_center" }
  ],

  "layer": [
    {
      "mark": { "type": "bar" },
      "encoding": {
        "x": {
          "field": "MesNombreAño",
          "type": "ordinal",
          "sort": { "field": "OrdenYM", "op": "min", "order": "ascending" },
          "axis": { "title": null, "labelAngle": 0 }
        },
        "xOffset": { "field": "Grupo" },
        "y": {
          "field": "Valor",
          "type": "quantitative",
          "stack": "zero",
          "axis": { "title": null, "labels": false, "ticks": false, "grid": false }
        },
        "order": { "field": "OrdenSegmento" },
        "color": {
          "field": "Etiqueta",
          "type": "nominal",
          "scale": {
            "domain": ["ACTIVOS", "PASIVOS", "PATRIMONIO"],
            "range": ["#0B1F70", "#9CB6FA", "#1FC4EC"]
          },
          "legend": {
            "orient": "top",
            "direction": "horizontal",
            "title": null,
            "anchor": "middle",
            "labelFontSize": 16,
            "labelColor": "#333333",
            "symbolSize": 160
          }
        },
        "tooltip": [
          { "field": "MesNombreAño", "type": "ordinal", "title": "Mes" },
          { "field": "Etiqueta", "type": "nominal", "title": "Cuenta" },
          { "field": "Valor_M_txt", "type": "nominal", "title": "Valor" }
        ]
      }
    },

    {
      "transform": [{ "filter": "datum.Etiqueta == ''ACTIVOS''" }],
      "mark": { "type": "text", "fontSize": 8, "baseline": "middle", "align": "center", "fill": "white" },
      "encoding": {
        "x": {
          "field": "MesNombreAño",
          "type": "ordinal",
          "sort": { "field": "OrdenYM", "op": "min", "order": "ascending" }
        },
        "xOffset": { "field": "Grupo" },
        "y": { "field": "Valor_mid", "type": "quantitative" },
        "text": { "field": "Valor_M_txt", "type": "nominal" }
      }
    },

    {
      "transform": [{ "filter": "datum.Etiqueta != ''ACTIVOS''" }],
      "mark": { "type": "text", "fontSize": 8, "baseline": "middle", "align": "center", "fill": "black" },
      "encoding": {
        "x": {
          "field": "MesNombreAño",
          "type": "ordinal",
          "sort": { "field": "OrdenYM", "op": "min", "order": "ascending" }
        },
        "xOffset": { "field": "Grupo" },
        "y": { "field": "Valor_center", "type": "quantitative" },
        "text": { "field": "Valor_M_txt", "type": "nominal" }
      }
    }
  ],

  "config": {
    "view": { "stroke": null },
    "axis": { "domain": false }
  }
}'
